// Denomination (number of decimals)
export type CurrencyDenomination = 0 | 1 | 2 | 3 | 4;

// ISO 639-1
type LocaleCode =
  `${Lowercase<string & `${Uppercase<string>[0]}${Uppercase<string>[1]}`>}-${Uppercase<string & `${Uppercase<string>[0]}${Uppercase<string>[1]}`>}`;

export type Currency = {
  currencyCode: CurrencyCode;
  englishCurrencyName: string;
  localCurrencyName: string;
  currencySymbol: string;
  countryRegionFlagEmoji: string;
  languageCode: LocaleCode;
  smallestCommonDenomination: number;
  acceptedCountries?: string[];
};
